import React, { useState } from "react";
import { Form, Button, Alert } from "react-bootstrap";
import FormContainer from "../components/FormContainer";
import { IoEyeOffOutline } from "react-icons/io5";
import { FaRegEye } from "react-icons/fa";
import { useNavigate } from "react-router-dom";
import axios from "axios"; // Ensure you have axios installed: npm install axios

const LoginScreen = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");

  const navigate = useNavigate(); // Hook to programmatically navigate

  const validatePassword = (password) => {
    const minLength = 8;
    const hasUpperCase = /[A-Z]/.test(password);
    const hasSymbol = /[!@#$%^&*(),.?":{}|<>]/.test(password);

    if (password.length < minLength) {
      return "Password must be at least 8 characters long.";
    }
    if (!hasUpperCase) {
      return "Password must contain at least one capital letter.";
    }
    if (!hasSymbol) {
      return "Password must contain at least one symbol.";
    }
    return "";
  };

  const submitHandler = async (e) => {
    e.preventDefault();
    const validationError = validatePassword(password);
    if (validationError) {
      setErrorMessage(validationError);
      return;
    }

    try {
      // Replace with your server's login URL
      const response = await axios.post("http://localhost:5000/api/auth/login", {
        email,
        password,
      });

      // Handle the server response
      if (response.data.success) {
        // Assuming success response has a token or user info
        localStorage.setItem("token", response.data.token); // Store token or necessary info
        navigate("/"); // Navigate to the home screen
      } else {
        setErrorMessage(response.data.message || "Login failed. Please try again.");
      }
    } catch (error) {
      setErrorMessage(error.response?.data?.message || "An error occurred. Please try again.");
    }
  };

  const passwordChangeHandler = () => {
    setShowPassword(!showPassword);
  };

  const passwordType = () => {
    return showPassword ? "text" : "password";
  };

  return (
    <FormContainer>
      <h1>Sign In</h1>
      {errorMessage && <Alert variant="danger">{errorMessage}</Alert>}
      <Form onSubmit={submitHandler}>
        <Form.Group controlId="email" className="my-3">
          <Form.Label>Email Address</Form.Label>
          <Form.Control
            type="email"
            placeholder="Enter email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          ></Form.Control>
        </Form.Group>
        <Form.Group controlId="password" className="my-3">
          <Form.Label onClick={passwordChangeHandler}>
            Password{" "}
            {showPassword ? (
              <FaRegEye className="mx-1" />
            ) : (
              <IoEyeOffOutline className="mx-1" />
            )}
          </Form.Label>
          <Form.Control
            type={passwordType()}
            placeholder="Enter password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          ></Form.Control>
        </Form.Group>
        <Button type="submit" variant="primary" className="mt-2">
          Sign in
        </Button>
      </Form>
    </FormContainer>
  );
};

export default LoginScreen;
